<script>
export default {
  name: 'homeTodo', // 多单词
  
  data() {
    return {
      isLoading: true,
      userInfo: {},
      todoInfo: {},
      apiBase: 'hgxt',
      apis: {
        queryUserRoleAndToDoNumber: '/business/queryUserRoleAndToDoNumber', // 姓名，代办数量
        queryAdminProjectCount: '/business/queryAdminProjectCount', // 管理员-代办数量
        queryZyProjectCount: '/business/queryZyProjectCount', // 专员-代办数量
        queryZjzMonthReportAndTaskData: '/business/queryZjzMonthReportAndTaskData', // 专兼职-查询专兼职人员未完成履职月报和任务数据
      },
    }
  },
  async mounted () {
    try {
      this.userInfo = await this.myAxios(this.apis.queryUserRoleAndToDoNumber, {})
      console.log(this.todoInfo, 'this.todoInfo')
      if (this.userInfo.role == 1) {// 管理员
        this.todoInfo = (await this.myAxios(this.apis.queryAdminProjectCount, {}))?.data?.[0] || {}
      } else if (this.userInfo.role == 2) { // 专员
        this.todoInfo = (await this.myAxios(this.apis.queryZyProjectCount, {}))?.data?.[0] || {}
      } else { // 专兼职
        this.todoInfo = (await this.myAxios(this.apis.queryZjzMonthReportAndTaskData, {}))?.data?.[0] || {}
      }
    } catch(err) {
      console.log(err, 'catch err')
    }

    this.isLoading = false;

  },
  methods: {
    gopage (path='', query={}) {
      console.log(path, 'path')
      if (!path) {
        return Error('没有跳转地址')
      }
      window?.gofun?.(path, query)
    },
    async myAxios(url='', param={}){
      return new Promise((resolve, reject) => {
        // 没有this.$util.ajaxFun使用axios
        let prdEnv = this?.$util?.ajaxFun
        if (prdEnv) {
          this.$util.ajaxFun(url, param, (res)=>{
            if (res.code === 200) {
              resolve(res.data)
            } else {
              console.log(res, '接口报错了，地址：', url)
              reject(res.data)
            }
          })
        } else {
          url = this.apiBase + url
          // 没有this.$util.ajaxFun使用axios
          this.$http.post(url, param).then(res => {
            if (res.data.code === 200) {
              resolve(res.data.data)
            } else {
              console.log(res, '接口报错了，地址：', url)
              reject(res.data)
            }
          })
        }
      })
    },
   
  }
}
</script>
